    if (window.ethereum) {
      try {
        const addressArray = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        const obj = {
          status: "ðŸ‘†ðŸ½ Write a message in the text-field above.",
          address: addressArray[0],
        };
        return obj;
      } catch (err) {
        return {
          address: "",
          status: "ðŸ˜¥ " + err.message,
        };
      }
    } else {
      return {
        address: "",
        status: ("Must install Metamask"), 
        }; 
    } 
};

export const getCurrentWalletConnected = async () => {
    if (window.ethereum) {
      try {
        const addressArray = await window.ethereum.request({
          method: "eth_accounts",
        });
        if (addressArray.length > 0) {
          return {
            address: addressArray[0],
            status: "ðŸ‘†ðŸ½ Write a message in the text-field above.",
          };
        } else {
          return {
            address: "",
            status: "ðŸ¦Š Connect to Metamask using the top right button.",
          };
        }
      } catch (err) {
        return {
          address: "",
          status: "ðŸ˜¥ " + err.message,
        };
      }
    } else {
      return {
        address: "",
        status: ("Must install Metamask"), 
    }; 
} 
};

export const updateMessage = async (address, message) => {
  
};

// Fetch contract variables
export const getContractVariables = async () => {
  try {
    const maxTokensPerWallet = await tokenContract.methods.maxTokensPerWallet().call();
    const sellTimeframe = await tokenContract.methods.sellTimeframe().call();
    const totalSupply = await tokenContract.methods.totalSupply().call();

    return { maxTokensPerWallet, sellTimeframe, totalSupply };
  } catch (err) {
    console.error(err);
    return { maxTokensPerWallet: 0, sellTimeframe: 0, totalSupply: 0 };
  }
};

// Burn tokens
export const burnTokens = async (amount) => {
  const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
  const account = accounts[0];
  try {
    const receipt = await tokenContract.methods.burn(amount).send({ from: account });
    return { success: true, receipt };
  } catch (err) {
    console.error(err);
    return { success: false, error: err.message };
  }
};

// Listen to contract events
export const addContractEventListeners = (eventHandlers) => {
  tokenContract.events.WalletChosen({}, eventHandlers.walletChosen);
  tokenContract.events.TimeframeUpdated({}, eventHandlers.timeframeUpdated);
  tokenContract.events.HolderListUpdated({}, eventHandlers.holderListUpdated);
  tokenContract.events.Burn({}, eventHandlers.burn);
};
