import React, { useState, useEffect } from "react";
import {
  connectWallet,
  getCurrentWalletConnected,
  burnTokens,
  addContractEventListeners,
} from "./util/interact.js";

const BurnTokenPage = () => {
  const [walletAddress, setWallet] = useState("");
  const [status, setStatus] = useState("");
  const [burnAmount, setBurnAmount] = useState("");

  useEffect(() => {
    const initialize = async () => {
      const { address, status } = await getCurrentWalletConnected();
      setWallet(address);
      setStatus(status);
      addSmartContractListener();
    };

    initialize();
  }, []);

  const addSmartContractListener = () => {
    addContractEventListeners({
      burn: (error, event) => {
        if (!error) console.log("Burn Event:", event.returnValues);
      },
    });
  };

  const connectWalletPressed = async () => {
    const walletResponse = await connectWallet();
    setWallet(walletResponse.address);
    setStatus(walletResponse.status);
  };

  const burnTokensPressed = async () => {
    if (!burnAmount || isNaN(burnAmount) || Number(burnAmount) <= 0) {
      alert("Please enter a valid number of tokens to burn.");
      return;
    }
    const result = await burnTokens(burnAmount);
    if (result.success) {
      alert("Tokens burned successfully!");
      setBurnAmount("");
    } else {
      alert(`Error burning tokens: ${result.error}`);
    }
  };

  return (
    <div className="burn-container">
      <h2>Burn Tokens</h2>
      <button onClick={connectWalletPressed}>
        {walletAddress.length > 0
          ? `Connected: ${walletAddress.substring(0, 6)}...${walletAddress.substring(38)}`
          : "Connect Wallet"}
      </button>
      <div>
        <input
          type="number"
          placeholder="Enter tokens to burn"
          value={burnAmount}
          onChange={(e) => setBurnAmount(e.target.value)}
        />
        <button onClick={burnTokensPressed}>Burn Tokens</button>
      </div>
      <p>{status}</p>
    </div>
  );
};

export default BurnTokenPage;
