import React, { useState } from 'react';
import FrontEndHome from './FrontEndHome';
import BurnTokenPage from './BurnTokenPage';
import './App.css';

function App() {
  const [activePage, setActivePage] = useState('home'); // Default to FrontEndHome

  return (
    <div className="App">
      {/* Menu Bar */}
      <nav style={{ padding: '10px', background: '#f0f0f0', display: 'flex', justifyContent: 'space-around' }}>
        <button onClick={() => setActivePage('home')} style={{ padding: '10px 20px' }}>
          Home
        </button>
        <button onClick={() => setActivePage('burn')} style={{ padding: '10px 20px' }}>
          Burn Token
        </button>
      </nav>

      {/* Page Content */}
      <div style={{ marginTop: '20px' }}>
        {activePage === 'home' && <FrontEndHome />}
        {activePage === 'burn' && <BurnTokenPage />}
      </div>
    </div>
  );
}

export default App;
